% Models correction for mirror by Cameronclear allradius  = 2.02/2; % µmlambda  = 0.6328; % µmnre_p   = 1.59;nre_med = 1.333;mus		= 11.88994;g		= 0.91235;s=(10/mus)/80;x=[-40:40]*s;y=[-40:40]*s;%----------------------------------------------------------%% H source%----------------------------------------------------------%load outHI.mathi 	= (outHI);clear outHIload outHQ.mathq 	= (outHQ);clear outHQload outHU.mathu	= (outHU);clear outHUload outHV.mathv 	= (outHV);clear outHVHH	= flipud(fliplr((hi+hq)/2));HV	= flipud(fliplr((hi-hq)/2));HP	= flipud(fliplr((hi-hu)/2)); % mirror rotation HR	= flipud(fliplr((hi-hv)/2)); % mirror rotation %----------------------------------------------------------% V source%----------------------------------------------------------%load outVI.matvi	= (outVI);clear outVI;load outVQ.matvq 	= ((outVQ));clear outVQ;load outVU.matvu 	= (outVU);clear outVU;load outVV.matvv 	= (outVV);clear outVV;VH	= flipud(fliplr((vi+vq)/2));VV	= flipud(fliplr((vi-vq)/2));VP	= flipud(fliplr((vi-vu)/2)); % mirror rotation VR	= flipud(fliplr((vi-vv)/2)); % mirror rotation %----------------------------------------------------------%% P source%----------------------------------------------------------%load outPI.matpi	= (outPI);clear outPI;load outPQ.matpq	= (outPQ);clear outPQ;load outPU.matpu	= (outPU);clear outPU;load outPV.matpv	= (outPV);clear outPV;PH	= flipud(fliplr((pi+pq)/2));PV	= flipud(fliplr((pi-pq)/2));PP	= flipud(fliplr((pi-pu)/2)); % mirror rotation PR	= flipud(fliplr((pi-pv)/2)); % mirror rotation %----------------------------------------------------------%% R source%----------------------------------------------------------%load outRI.matri = (outRI);clear outRI;load outRQ.matrq = ((outRQ));clear outRQ;load outRU.matru = (outRU);clear outRU;load outRV.matrv = (outRV);clear outRV;RH=flipud(fliplr((ri+rq)/2));RV=flipud(fliplr((ri-rq)/2));RP=flipud(fliplr((ri-ru)/2)); % mirror rotation RR=flipud(fliplr((ri-rv)/2)); % mirror rotation %----------------------------------------------------------%% Mueller generation%----------------------------------------------------------%m11=HH+HV+VH+VV; m12=HH+HV-VH-VV; m13=2*PH+2*PV-m11; m14=2*RH+2*RV-m11;m21=HH-HV+VH-VV; m22=HH-HV-VH+VV; m23=2*PH-2*PV-m21; m24=2*RH-2*RV-m21; m31=2*HP+2*VP-m11; m32=2*HP-2*VP-m12; m33=4*PP-2*PH-2*PV-m31; m34=4*RP-2*RH-2*RV-m31; m41=2*HR+2*VR-m11; m42=2*HR-2*VR-m12; m43=4*PR-2*PH-2*PV-m41; m44=4*RR-2*RH-2*RV-m41; x=[-20:20]*s;y=[-20:20]*s;miin=-5e-5;maax=5e-5;set(gca,'fontsize',15)set(colorbar,'fontsize',15)if 0 %set this to 1 if you want to select a different m11 area'grab a section of the m11 image just off the center'    figure    imagesc(m11)    a=imcrop;    norm=mean2(a);end%norm=1.2e3;norm=1.2e3;rest=[-1e-1 1e-1];figure(1);clfsubplot(4,4,1)imagesc(x,y,m11/norm,rest*10);set(gca,'Xtick',[]);set(gca,'Ytick',[]);axis('square')colorbarsubplot(4,4,2)imagesc(x,y,m12/norm,rest);set(gca,'Xtick',[]);set(gca,'Ytick',[]);colorbaraxis('square')colorbarsubplot(4,4,3)imagesc(x,y,m13/norm,rest);set(gca,'Xtick',[]);set(gca,'Ytick',[]);axis('square')colorbarsubplot(4,4,4)imagesc(x,y,m14/norm,rest);set(gca,'Xtick',[]);set(gca,'Ytick',[]);axis('square')colorbarsubplot(4,4,5)imagesc(x,y,m21/norm,rest);set(gca,'Xtick',[]);set(gca,'Ytick',[]);axis('square')colorbarsubplot(4,4,6)imagesc(x,y,m22/norm,rest);set(gca,'Xtick',[]);set(gca,'Ytick',[]);axis('square')colorbarsubplot(4,4,7)imagesc(x,y,m23/norm,rest);set(gca,'Xtick',[]);set(gca,'Ytick',[]);axis('square')colorbarsubplot(4,4,8)imagesc(x,y,m24/norm,rest);set(gca,'Xtick',[]);set(gca,'Ytick',[]);axis('square')colorbarsubplot(4,4,9)imagesc(x,y,m31/norm,rest);set(gca,'Xtick',[]);set(gca,'Ytick',[]);axis('square')colorbarsubplot(4,4,10)imagesc(x,y,m32/norm,rest);set(gca,'Xtick',[]);set(gca,'Ytick',[]);axis('square')colorbarsubplot(4,4,11)imagesc(x,y,m33/norm,rest);set(gca,'Xtick',[]);set(gca,'Ytick',[]);axis('square')colorbarsubplot(4,4,12)imagesc(m34/norm,rest);set(gca,'Xtick',[]);set(gca,'Ytick',[]);axis('square')colorbarsubplot(4,4,13)imagesc(x,y,m41/norm,rest);set(gca,'Xtick',[]);set(gca,'Ytick',[]);axis('square')colorbarsubplot(4,4,14)imagesc(x,y,m42/norm,rest);set(gca,'Xtick',[]);set(gca,'Ytick',[]);axis('square')colorbarsubplot(4,4,15)imagesc(x,y,m43/norm,rest);set(gca,'Xtick',[]);set(gca,'Ytick',[]);axis('square')colorbarsubplot(4,4,16)imagesc(x,y,m44/norm,rest*10);set(gca,'Xtick',[]);set(gca,'Ytick',[]);axis('square')colorbar